  #undef alphaa
  #undef ma
  #undef Wa
  #undef R0i
  #undef Qci
  #undef alphai
  #undef mi
  #undef Wi
  #undef R0s
  #undef Qcs
  #undef alphas
  #undef ms
  #undef Ws
